OPENQASM 2.0;
include "qelib1.inc";

qreg q[12];
creg c[12];

// Step 1: Initialize superposition
h q[0];
h q[1];
h q[2];
h q[3];
h q[4];
h q[5];
h q[6];
h q[7];
h q[8];
h q[9];
h q[10];
h q[11];

// Step 2: Hamiltonian (~8 Trotter steps, ~80 gates)
// Kinetic (RZ, pi/4) and Potential (CZ + RZ with Ramanujan weights)
rz(pi/4) q[0];
rz(pi/4) q[1];
rz(pi/4) q[2];
rz(pi/4) q[3];
rz(pi/4) q[4];
rz(pi/4) q[5];
rz(pi/4) q[6];
rz(pi/4) q[7];
rz(pi/4) q[8];
rz(pi/4) q[9];
rz(pi/4) q[10];
rz(pi/4) q[11];

// Trotter Step 1: w(0) = 1103/396^0 = 1103
cz q[0], q[1];
rz(0.11) q[1];  // k = 0.1, w(0) ~ 1103 -> 0.11 rad
cz q[1], q[2];
rz(0.11) q[2];
cz q[2], q[3];
rz(0.11) q[3];
cz q[3], q[4];
rz(0.11) q[4];
cz q[4], q[5];
rz(0.11) q[5];
cz q[5], q[6];
rz(0.11) q[6];
cz q[6], q[7];
rz(0.11) q[7];
cz q[7], q[8];
rz(0.11) q[8];
cz q[8], q[9];
rz(0.11) q[9];
cz q[9], q[10];
rz(0.11) q[10];
cz q[10], q[11];
rz(0.11) q[11];

// Step 2: w(1) = (1103 + 26390)/396^4 ~ 0.00107
rz(pi/4) q[0];
rz(pi/4) q[1];
rz(pi/4) q[2];
rz(pi/4) q[3];
rz(pi/4) q[4];
rz(pi/4) q[5];
rz(pi/4) q[6];
rz(pi/4) q[7];
rz(pi/4) q[8];
rz(pi/4) q[9];
rz(pi/4) q[10];
rz(pi/4) q[11];

cz q[0], q[1];
rz(0.0001) q[1];  // k = 0.1, w(1) ~ 0.00107 -> 0.0001 rad
cz q[1], q[2];
rz(0.0001) q[2];
cz q[2], q[3];
rz(0.0001) q[3];
cz q[3], q[4];
rz(0.0001) q[4];

// Measurement
measure q[0] -> c[0];
measure q[1] -> c[1];
measure q[2] -> c[2];
measure q[3] -> c[3];
measure q[4] -> c[4];
measure q[5] -> c[5];
measure q[6] -> c[6];
measure q[7] -> c[7];
measure q[8] -> c[8];
measure q[9] -> c[9];
measure q[10] -> c[10];
measure q[11] -> c[11];
