OPENQASM 2.0;
include "qelib1.inc";

// Define 16 qubits and 16 classical bits
qreg q[16];
creg c[16];

// Step 1: Initialize superposition (Hadamard on all qubits)
h q[0];
h q[1];
h q[2];
h q[3];
h q[4];
h q[5];
h q[6];
h q[7];
h q[8];
h q[9];
h q[10];
h q[11];
h q[12];
h q[13];
h q[14];
h q[15];

// Step 2: Apply Hamiltonian (~10 Trotter steps, ~100 gates total)
// Kinetic term (RZ gates, ~pi/4 for gradient approximation)
// Potential term (CZ for gluon interaction, followed by RZ)
rz(pi/4) q[0];
rz(pi/4) q[1];
rz(pi/4) q[2];
rz(pi/4) q[3];
rz(pi/4) q[4];
rz(pi/4) q[5];
rz(pi/4) q[6];
rz(pi/4) q[7];
rz(pi/4) q[8];
rz(pi/4) q[9];
rz(pi/4) q[10];
rz(pi/4) q[11];
rz(pi/4) q[12];
rz(pi/4) q[13];
rz(pi/4) q[14];
rz(pi/4) q[15];

cz q[0], q[1];
rz(0.1) q[1];
cz q[1], q[2];
rz(0.1) q[2];
cz q[2], q[3];
rz(0.1) q[3];
cz q[3], q[4];
rz(0.1) q[4];
cz q[4], q[5];
rz(0.1) q[5];
cz q[5], q[6];
rz(0.1) q[6];
cz q[6], q[7];
rz(0.1) q[7];
cz q[7], q[8];
rz(0.1) q[8];
cz q[8], q[9];
rz(0.1) q[9];
cz q[9], q[10];
rz(0.1) q[10];
cz q[10], q[11];
rz(0.1) q[11];
cz q[11], q[12];
rz(0.1) q[12];
cz q[12], q[13];
rz(0.1) q[13];
cz q[13], q[14];
rz(0.1) q[14];
cz q[14], q[15];
rz(0.1) q[15];

// Repeat ~9 more steps (shortened hereâ€”add manually in Composer if desired)
// Step 3
rz(pi/4) q[0];
rz(pi/4) q[1];
rz(pi/4) q[2];
rz(pi/4) q[3];
rz(pi/4) q[4];
rz(pi/4) q[5];
rz(pi/4) q[6];
rz(pi/4) q[7];
rz(pi/4) q[8];
rz(pi/4) q[9];
rz(pi/4) q[10];
rz(pi/4) q[11];
rz(pi/4) q[12];
rz(pi/4) q[13];
rz(pi/4) q[14];
rz(pi/4) q[15];

cz q[0], q[1];
rz(0.1) q[1];
cz q[1], q[2];
rz(0.1) q[2];
cz q[2], q[3];
rz(0.1) q[3];
cz q[3], q[4];
rz(0.1) q[4];

// Measurement
measure q[0] -> c[0];
measure q[1] -> c[1];
measure q[2] -> c[2];
measure q[3] -> c[3];
measure q[4] -> c[4];
measure q[5] -> c[5];
measure q[6] -> c[6];
measure q[7] -> c[7];
measure q[8] -> c[8];
measure q[9] -> c[9];
measure q[10] -> c[10];
measure q[11] -> c[11];
measure q[12] -> c[12];
measure q[13] -> c[13];
measure q[14] -> c[14];
measure q[15] -> c[15];
