OPENQASM 2.0;
include "qelib1.inc";

// Define 16 qubits and 16 classical bits
qreg q[16];
creg c[16];

// Past: Free quark propagation (-½ D^μD_μ)
h q[0];  // Quark 1
h q[1];  // Quark 2
h q[2];  // Quark 3
h q[3];  // Quark 4
h q[4];  // Quark 5
h q[5];  // Quark 6
h q[6];  // Quark 7
h q[7];  // Quark 8
barrier q[0], q[1], q[2], q[3], q[4], q[5], q[6], q[7], q[8], q[9], q[10], q[11], q[12], q[13], q[14], q[15];

// Present: Quark confinement begins (g²|A|²)
cx q[0], q[8];   // Confine q[0] to q[8]
cx q[1], q[9];   // Confine q[1] to q[9]
cx q[2], q[10];  // Confine q[2] to q[10]
cx q[3], q[11];  // Confine q[3] to q[11]
cx q[4], q[12];  // Confine q[4] to q[12]
cx q[5], q[13];  // Confine q[5] to q[13]
cx q[6], q[14];  // Confine q[6] to q[14]
cx q[7], q[15];  // Confine q[7] to q[15]
barrier q[0], q[1], q[2], q[3], q[4], q[5], q[6], q[7], q[8], q[9], q[10], q[11], q[12], q[13], q[14], q[15];

// Future: Quark confinement with flux (Λ_QCD·f(t))
rz(0.005) q[14];  // Flux phase on q[14] (200 MeV)
x q[14];          // Confinement flip q[14]
rz(0.005) q[15];  // Flux phase on q[15]
x q[15];          // Confinement flip q[15]
barrier q[0], q[1], q[2], q[3], q[4], q[5], q[6], q[7], q[8], q[9], q[10], q[11], q[12], q[13], q[14], q[15];

// Measure all qubits to reflect quark gap
measure q[0] -> c[0];
measure q[1] -> c[1];
measure q[2] -> c[2];
measure q[3] -> c[3];
measure q[4] -> c[4];
measure q[5] -> c[5];
measure q[6] -> c[6];
measure q[7] -> c[7];
measure q[8] -> c[8];
measure q[9] -> c[9];
measure q[10] -> c[10];
measure q[11] -> c[11];
measure q[12] -> c[12];
measure q[13] -> c[13];
measure q[14] -> c[14];
measure q[15] -> c[15];
