OPENQASM 2.0;
include "qelib1.inc";

// Define 32 qubits and 32 classical bits
qreg q[32];
creg c[32];

// Past: Free quark propagation (-½ D^μD_μ)
h q[0];   // Quark 1
h q[1];   // Quark 2
h q[2];   // Quark 3
h q[3];   // Quark 4
