cx q[8], q[24];   // Confine q[8] to q[24]
cx q[9], q[25];   // Confine q[9] to q[25]
cx q[10], q[26];  // Confine q[10] to q[26]
cx q[11], q[27];  // Confine q[11] to q[27]
cx q[12], q[28];  // Confine q[12] to q[28] (note: adjusted due to 32 qubits)
cx q[13], q[29];  // Confine q[13] to q[29]
cx q[14], q[30];  // Confine q[14] to q[30]
cx q[15], q[31];  // Confine q[15] to q[31]
barrier q[0], q[1], q[2], q[3], q[4], q[5], q[6], q[7], q[8], q[9], q[10], q[11], q[12], q[13], q[14], q[15], 
        q[16], q[17], q[18], q[19], q[20], q[21], q[22], q[23], q[24], q[25], q[26], q[27], q[28], q[29], q[30], q[31];

// Future: Quark confinement with flux (Λ_QCD·f(t))
rz(0.005) q[28];  // Flux phase on q[28] (200 MeV)
x q[28];          // Confinement flip q[28]
rz(0.005) q[29];  // Flux phase on q[29]
x q[29];          // Confinement flip q[29]
rz(0.005) q[30];  // Flux phase on q[30]
x q[30];          // Confinement flip q[30]
rz(0.005) q[31];  // Flux phase on q[31]
x q[31];          // Confinement flip q[31]
barrier q[0], q[1], q[2], q[3], q[4], q[5], q[6], q[7], q[8], q[9], q[10], q[11], q[12], q[13], q[14], q[15], 
        q[16], q[17], q[18], q[19], q[20], q[21], q[22], q[23], q[24], q[25], q[26], q[27], q[28], q[29], q[30], q[31];

// Measure all qubits to reflect quark gap
measure q[0] -> c[0];
