OPENQASM 2.0;
include "qelib1.inc";

// Define 8 qubits and 8 classical bits
qreg q[8];
creg c[8];

// Past: Free quark propagation (-½ D^μD_μ)
h q[0];  // Quark 1
h q[1];  // Quark 2
h q[2];  // Quark 3
h q[3];  // Quark 4
barrier q[0], q[1], q[2], q[3], q[4], q[5], q[6], q[7];

// Present: Quark confinement begins (g²|A|²)
cx q[0], q[4];  // Confine q[0] to q[4]
cx q[1], q[5];  // Confine q[1] to q[5]
cx q[2], q[6];  // Confine q[2] to q[6]
cx q[3], q[7];  // Confine q[3] to q[7]
barrier q[0], q[1], q[2], q[3], q[4], q[5], q[6], q[7];

// Future: Quark confinement with flux (Λ_QCD·f(t))
rz(0.005) q[4];  // Flux phase on confined q[4] (200 MeV)
x q[4];          // Confinement flip q[4]
rz(0.005) q[5];  // Flux phase on confined q[5]
x q[5];          // Confinement flip q[5]
rz(0.005) q[6];  // Flux phase on confined q[6]
x q[6];          // Confinement flip q[6]
rz(0.005) q[7];  // Flux phase on confined q[7]
x q[7];          // Confinement flip q[7]
barrier q[0], q[1], q[2], q[3], q[4], q[5], q[6], q[7];

// Measure all qubits to reflect quark gap
measure q[0] -> c[0];
measure q[1] -> c[1];
measure q[2] -> c[2];
measure q[3] -> c[3];
measure q[4] -> c[4];
measure q[5] -> c[5];
measure q[6] -> c[6];
measure q[7] -> c[7];
