OPENQASM 2.0;
include "qelib1.inc";

// 5 qubits (2x2x1 lattice) and 5 classical bits
qreg q[5];
creg c[5];

// Step 1: Initialize superposition
h q[0];
h q[1];
h q[2];
h q[3];
h q[4];

// Step 2: Hamiltonian (~5 steps, ~40 gates total)
// Kinetic (RZ) and Potential (CZ + RZ)
rz(pi/4) q[0];
rz(pi/4) q[1];
rz(pi/4) q[2];
rz(pi/4) q[3];
rz(pi/4) q[4];

cz q[0], q[1];
rz(0.1) q[1];
cz q[1], q[2];
rz(0.1) q[2];
cz q[2], q[3];
rz(0.1) q[3];
cz q[3], q[4];
rz(0.1) q[4];

// Repeat ~4 more steps (shortened here—add ~2–3 in Composer)
rz(pi/4) q[0];
rz(pi/4) q[1];
rz(pi/4) q[2];
rz(pi/4) q[3];
rz(pi/4) q[4];

cz q[0], q[1];
rz(0.1) q[1];
cz q[1], q[2];
rz(0.1) q[2];

// Measurement
measure q[0] -> c[0];
measure q[1] -> c[1];
measure q[2] -> c[2];
measure q[3] -> c[3];
measure q[4] -> c[4];
