OPENQASM 2.0;
include "qelib1.inc";

// Define 3 qubits and 3 classical bits
qreg q[3];
creg c[3];

// Past: Star light emission (photon in superposition)
h q[0];

// Present: Light reaches observer (entanglement and collapse)
cx q[0], q[1];

// Future: Quark position (flux and confinement)
s q[2];         // Phase shift (Λ_QCD·f(t) approximation)
x q[2];         // Confinement potential (g²|A|² approximation)

// Measure all qubits to reflect nullification and re-quantification
measure q[0] -> c[0];
measure q[1] -> c[1];
measure q[2] -> c[2];
