OPENQASM 2.0;
include "qelib1.inc";

// Define 3 qubits and 3 classical bits
qreg q[3];
creg c[3];

// Past: Star light emission (kinetic term: -½ D^μD_μ)
h q[0];         // Superposition for photon wave (e.g., 2 eV)
barrier q[0], q[1], q[2];

// Present: Observation (entanglement and collapse)
cx q[0], q[1];  // Light interacts with observer, nullifies (^ → 0)
barrier q[0], q[1], q[2];

// Future: Quark position (flux and confinement)
rz(0.01) q[2];  // Flux term Λ_QCD·f(t), θ ≈ 0.01 rad (200 MeV scale)
x q[2];         // Confinement potential g²|A|² (quark state flip)
barrier q[0], q[1], q[2];

// Measure to reflect ∞/^ = 0 cycle
measure q[0] -> c[0];
measure q[1] -> c[1];
measure q[2] -> c[2];
