OPENQASM 2.0;
include "qelib1.inc";

// Define 8 qubits and 8 classical bits
qreg q[8];
creg c[8];

// Step 1: Initialize superposition
h q[0];
h q[1];
h q[2];
h q[3];
h q[4];
h q[5];
h q[6];
h q[7];

// Step 2: Hamiltonian (5 Trotter steps, ~60 gates total)
// Kinetic (RZ) and Potential (CZ + RZ)
rz(pi/4) q[0];
rz(pi/4) q[1];
rz(pi/4) q[2];
rz(pi/4) q[3];
rz(pi/4) q[4];
rz(pi/4) q[5];
rz(pi/4) q[6];
rz(pi/4) q[7];

cz q[0], q[1];
rz(0.1) q[1];
cz q[1], q[2];
rz(0.1) q[2];
cz q[2], q[3];
rz(0.1) q[3];
cz q[3], q[4];
rz(0.1) q[4];
cz q[4], q[5];
rz(0.1) q[5];
cz q[5], q[6];
rz(0.1) q[6];
cz q[6], q[7];
rz(0.1) q[7];

// Repeat 4 more times (simplified hereâ€”add manually in Composer if desired)
rz(pi/4) q[0];
rz(pi/4) q[1];
rz(pi/4) q[2];
rz(pi/4) q[3];
rz(pi/4) q[4];
rz(pi/4) q[5];
rz(pi/4) q[6];
rz(pi/4) q[7];

cz q[0], q[1];
rz(0.1) q[1];
cz q[1], q[2];
rz(0.1) q[2];

// Measurement
measure q[0] -> c[0];
measure q[1] -> c[1];
measure q[2] -> c[2];
measure q[3] -> c[3];
measure q[4] -> c[4];
measure q[5] -> c[5];
measure q[6] -> c[6];
measure q[7] -> c[7];
